@Component({
  selector: 'app-input',
  templateUrl: './input.component.html',
  styleUrls: ['./input.component.scss']
})
export class InputComponent {

}
<div class="container">
    <div class="placeholder">placeholder</div>
    <div class="input">
        <img src="/assets/icons/angular.svg" alt="" class="icon" />
        <input type="text" />
    </div>
</div>
